package sevice;

import model.Product;

import java.util.ArrayList;
import java.util.List;

public class ProductService {
    public static List<Product> products = new ArrayList<>();

    static {
        products.add(new Product(1, "hoa", "data:image/jpeg;base64,", 500));
        products.add(new Product(2, "lan", "data:image/jpeg;base64,", 500));
        products.add(new Product(3, "hoa", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTYdZ3nPf99sKQUtNUFmwJeDN-4vTYnF8depg&usqp=CAU", 500));
        products.add(new Product(4, "hoa", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSLJoiHNFjYCR515BB-Y7y7Ab-wR8F1zWxeiQ&usqp=CAU", 500));
    }

    public static void add(Product product) {
        products.add(product);
    }

    public static void edit(int id, Product product) {
        for (int i = 0; i < products.size(); i++) {
            if (products.get(i).getId() == id) {
                products.set(i, product);
            }
        }
    }

    public static void delete(int id) {
        for (int i = 0; i < products.size(); i++) {
            if (products.get(i).getId() == id) {
                products.remove(i);
            }
        }
    }
}
